import React, { useState, useEffect } from "react";
import "./Booking.css";
import {
  Typography,
  Button,
  Grid,
  Select,
  MenuItem,
} from "@material-ui/core";

import Modal from "react-modal";


Modal.setAppElement(document.getElementById("root"));


const Booking = ({ baseUrl, isLogin }) => {
  const [startingPoint, setStartingPoint] = useState("");
  const [destination, setDestination] = useState("");
  const [startingPointList, setStartingPointList] = useState([]);
  const [destinationList, setDestinationList] = useState([]);
  const [booking, setBooking] = useState("");


  const getStartingPoint = async () => {
    const url = baseUrl + "bus/startingPoint";


    try {
      const rawResponse = await fetch(url, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        }
      });

      if (rawResponse.ok) {
        const response = await rawResponse.json();
        await setStartingPointList(response);
      } else {
        const error = new Error();
        error.message = "Some Error Occurred while fetching source";
        throw error;
      }
    } catch (e) {
      alert(e.message);
    }
  };

  const getDestination = async () => {
    const url = baseUrl + "bus/destination";

    try {
      const rawResponse = await fetch(url, {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        }
      });

      if (rawResponse.ok) {
        const response = await rawResponse.json();
        await setDestinationList(response);
      } else {
        const error = new Error();
        error.message = "Some Error Occurred while fetching destination";
        throw error;
      }
    } catch (e) {
      alert(e.message);
    }
  };



  const changeStartingPointHandler = (event) => {
    setStartingPoint(event.target.value);

  };

  const changeDestinationHandler = (event) => {
    setDestination(event.target.value);
  };

  // const closeModalHandler = () => {
  //   setIsModalOpen(false);
  // };
  useEffect(() => {
    if (isLogin === true) {
      getDestination();
      getStartingPoint();
    }
  }, [isLogin]);

  return (
    <div>
      <form>
        <Grid item xs={12} sm container alignItems="center" direction="column">
          <Typography component="div" id="selectHeading">
            Select Starting Point:
          </Typography>
          <Select
            variant="filled"
            labelId="speciality"
            id="speciality"
            value={startingPoint}
            style={{ minWidth: "200px" }}
            onChange={changeStartingPointHandler}
          >
            <MenuItem key={"spec none"} value={""}>
              NONE
            </MenuItem>
            {startingPointList.map((item) => (
              <MenuItem key={"spec" + item} value={item}>
                {item}
              </MenuItem>
            ))}
          </Select>

          <Typography component="div" id="selectHeading">
            Select Destination:
          </Typography>
          <Select
            variant="filled"
            labelId="speciality"
            id="speciality"
            value={destination}
            style={{ minWidth: "200px" }}
            onChange={changeDestinationHandler}
          >
            <MenuItem key={"spec none"} value={""}>
              NONE
            </MenuItem>

            {destinationList.map((item) => (
              <MenuItem key={"spec" + item} value={item}>
                {item}
              </MenuItem>
            ))}

          </Select>


          <Button
            style={{ width: "40%", margin: "10px" }}
            variant="contained"
            color="primary"
            onClick={() => {
              setBooking(booking);

            }}

          >

            Book Ticket
          </Button>



        </Grid>
      </form>
    </div >
  );
};

export default Booking;
